mapper.select:
select count(*)
from alarm_record ar
where ar.device_id = #{deviceId,jdbcType=INTEGER}
  AND TIMESTAMPDIFF(SECOND, ar.alarm_time, CURRENT_TIMESTAMP) <= 10;

mapper.update:
update alarm_record
alarm_time = #{alarmTime}, alarm_type = #{alarmType},
where device_id = #{deviceId}

update alarm_record
set alarm_status = '1',
    alarm_desc   = #{alarmDesc,jdbcType=VARCHAR},
    handle_time  = #{handleTime,jdbcType=TIMESTAMP}
where alarm_record_id = #{alarmRecordId,jdbcType=VARCHAR}
